!function(){for(var t=document.querySelectorAll(".video-wrap"),o=0;o<t.length;o++)t[o].addEventListener("click",function(){var t=document.createElement("iframe");t.setAttribute("frameborder","0"),t.setAttribute("allowfullscreen",""),t.setAttribute("src","https://player.vimeo.com/video/"+this.dataset.embed+"?autoplay=1&loop=1&title=0&byline=0&portrait=0"),this.innerHTML="",this.appendChild(t)})}();var scrollButton=document.getElementById("scrollBtn");function scrollFunction(){document.body.scrollTop>20||document.documentElement.scrollTop>20?scrollButton.classList.add("show"):scrollButton.classList.remove("show")}function topFunction(){document.body.scrollTop=0,document.documentElement.scrollTop=0}window.onscroll=function(){scrollFunction()};